import { Injectable, Component, OnInit, EventEmitter, Output } from '@angular/core';
import { Subscription } from 'rxjs';
import { Contact } from '../contact.model';
import { ContactService } from '../contact.service';

@Component({
  selector: 'app-contact-list',
  templateUrl: './contact-list.component.html',
  styleUrls: ['./contact-list.component.css']
})
export class ContactListComponent implements OnInit {

  contacts: Contact[];
  private subscription: Subscription;
  term: string;

  constructor(private contactService: ContactService) { }

  async ngOnInit() {
    this.contacts = await this.contactService.getContacts();
    this.subscription = this.contactService.contactChangedEvent.subscribe((contacts: Contact[]) => {
      this.contacts = contacts;
    });
  }
  ngOnDestroy(): void {
    this.subscription.unsubscribe();
  }

  search(value: string){
    this.term = value;
  }

}

<div class="row">
  <div class="col-xs-12">
    <div class="panel panel-default">
      <div class="panel-heading">
        <div class="row">
          <span class="title pad-left-right">Contacts</span>
        </div>
        <div class="row">
          <div class="input-group pull-left pad-left-right col-xs-7">
            <input type="text" #searchBox (keyup)="search(searchBox.value)" class="form-control" placeholder="Enter a search value">
            <div class="glyphicon glyphicon-search input-group-addon" (click)="search(searchBox.value)"></div>
          </div>
          <div class="pull-right pad-left-right">
            <a class="btn btn-success" [routerLink]="['new']">New Contact</a>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
<div class="row">
  <div class="col-xs-12">
    <app-contact-item *ngFor="let contactEle of contacts | contactsFilter:term let i = index" [contact]="contactEle" [index]="i"
    dnd-draggable [dragEnabled]="true" [dragData]="contactEle" [dropZones]="['contactGroup']"
    ></app-contact-item>
  </div>
</div>
